import React, {Fragment} from "react"
import ReactDom from "react-dom"
import Button from "../UI/Button"

const Backdrop = (props) => {
    return(
        <div></div>
    )
}

const Details = (props) => {
    return(
        <Fragment>
            <div>

            </div>
            <div>
                <h4>Total Amount:</h4>
                <p>$456</p>
            </div>
            <div>
                <Button>Close</Button>
                <Button>Order</Button>
            </div>
        </Fragment>
    )
}

const ShoppingCartDetails = (props) => {
    return(
        <Fragment>
            {ReactDom.createPortal(
              <Backdrop />
            )}
        </Fragment> 
    )
}

export default ShoppingCartDetails
