import react from "react"
import Wrapper from "../UI/Wrapper"
import Button from "../UI/Button"

import styles from "./Menu.module.css"

const Menu = (props) => {
    const menuData = [
        {
            name: 'Sushi',
            describtion: 'Finnest fish and veggies',
            price: 22.99,
            id: 'm1'
        },
        {
            name: 'Pasta',
            describtion: 'Finnest fish and veggies',
            price: 12.59,
            id: 'm2'
        },
        {
            name: 'Pizza',
            describtion: 'Finnest fish and veggies',
            price: 17.45,
            id: 'm3'
        },
        {
            name: 'Steak',
            describtion: 'Finnest fish and veggies',
            price: 35.99,
            id: 'm4'
        },{
            name: 'Rizzotto',
            describtion: 'Finnest fish and veggies',
            price: 14.60,
            id: 'm5'
        }
    ]

    const mealHendler = (event) => {
        event.preventDefault()
        const el = event.target
        // const name = el.getElementById('name')
        console.log(event.target)
    }
    
    return(
        <Wrapper className={styles.flex}>
            <div className={styles['menu-container']}>
                {menuData.map(item => {
                    return(
                        <form className={styles['meal-container']} key={item.id} onSubmit={mealHendler}>
                            <div className={styles.meal}>
                                <h4 className={styles.name} id="name">{item.name}</h4>
                                <p className={styles.describtion}>{item.describtion}</p>
                                <p className={styles.price}>$ {item.price}</p>
                            </div>
                            <div className={styles.controls}>
                                <label htmlFor="quantity">Amount:</label>
                                <input className={styles.input} type="number" min="0" id="quantity" placeholder="0" />
                                <Button type="submit" className={styles.button}>+ Add</Button>
                            </div>
                        </form>
                    )
                })}
            </div>
        </Wrapper>
    )
}

export default Menu
