import react from "react"
import Wrapper from "../UI/Wrapper"

import styles from "./Menu.module.css"

const Menu = (props) => {
    const menuData = [
        {
            name: 'Sushi',
            describtion: 'Finnest fish and veggies',
            price: 22.99,
            id: 'm1'
        },
        {
            name: 'Pasta',
            describtion: 'Finnest fish and veggies',
            price: 12.59,
            id: 'm2'
        },
        {
            name: 'Pizza',
            describtion: 'Finnest fish and veggies',
            price: 17.45,
            id: 'm3'
        },
        {
            name: 'Steak',
            describtion: 'Finnest fish and veggies',
            price: 35.99,
            id: 'm4'
        },{
            name: 'Rizzotto',
            describtion: 'Finnest fish and veggies',
            price: 14.60,
            id: 'm5'
        }
    ]
    
    return(
        <Wrapper className>
            <div className={styles['menu-container']}>
                {menuData.map(item => {
                    return(
                        <div className={styles['meal-container']} id={item.id}>
                            <div className={styles.meal}>
                                <h4 className={styles.name}>{item.name}</h4>
                                <p className={styles.describtion}>{item.describtion}</p>
                                <p className={styles.price}>$ {item.price}</p>
                            </div>
                            <div className={styles.form}>
                                <form>
                                    <label htmlFor="quantity">Amount:</label>
                                    <input type="number" min="0" id="quantity" />
                                </form>
                            </div>
                        </div>
                    )
                })}
            </div>
        </Wrapper>
    )
}

export default Menu
