import React, {Fragment} from "react"
import ReactDom from "react-dom"
import Button from "../UI/Button"

import styles from "./ShoppingCartDetails.module.css"

const Backdrop = (props) => {
    return(
        <div className={styles.backdrop}></div>
    )
}

const Details = (props) => {
    return(
        <div className={styles.details}>
            <div>

            </div>
            <div className={styles.total}>
                <h4>Total Amount:</h4>
                <p>$456</p>
            </div>
            <div className={styles.buttons}>
                <Button onClick={props.onShow} className>Close</Button>
                <Button>Order</Button>
            </div>
        </div>
    )
}

const ShoppingCartDetails = (props) => {
    return(
        <Fragment>
            {ReactDom.createPortal(
              <Backdrop />,
              document.getElementById('backdrop')
            )}
            {ReactDom.createPortal(
              <Details onShow={props.onShow} />,
              document.getElementById('shopping-cart-details')
            )}
        </Fragment> 
    )
}

export default ShoppingCartDetails
