import React, {useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [moviesData, setMoviesData] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState('')

  const fetchMoviesHandler = () => {
    setIsLoading(true)
    fetch('https://swapi.dev/api/film/')
      .then(response => {
        if (!response.ok) {
          throw new Error('Something went wrong!');
        }
        return response.json()
      })
      .then((data) => {
        const transformedMovies = data.results.map((movieData) => {
          return {
            id: movieData.episode_id,
            title: movieData.title,
            openingText: movieData.opening_crawl,
            releaseDate: movieData.release_date,
          };
        });
        setMoviesData(transformedMovies);
        setIsLoading(false)
      }).catch(e => {
        setError(e.message)
        console.log(e.message)
      })
  }

  // const fetchMoviesHandler = async() => {
  //   setIsLoading(true)
  //   const response = await fetch('https://swapi.dev/api/films/')
  //   const data = await response.json()

  //   const transformedMovies = data.results.map(movieData => {
  //     return {
  //       id: movieData.episode_id,
  //       title: movieData.title,
  //       openingText: movieData.opening_crawl,
  //       releaseDate: movieData.release_date,
  //     }
  //   })

  //   setMoviesData(transformedMovies)
  //   setIsLoading(false)
  // }

  let content = <p>No movies found</p>

  if(moviesData.length > 0) {
    return <MoviesList movies={moviesData} />
  }

  if(isLoading) {
    return <p>Loading...</p>
  }

  if(error) {
    return <p></p>
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && moviesData.length > 0 && <MoviesList movies={moviesData} />}
        {!isLoading && moviesData.length === 0 && <p>Found no movies.</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
