import React, {useState} from 'react'
import useInput from '../hooks/use-input'

const SimpleInput = (props) => {
  // const [nameInputValue, setNameInputValue] = useState('')
  // const [enteredNameTouched, setEnteredNameTouched] = useState(false)
  
  const [emailInputValue, setEmailInputValue] = useState('')
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false)

  const enteredNameIsValid = nameInputValue.trim() !== ''
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched
  
  const enteredEmailIsValid = emailInputValue.match(/^[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/)
  const emailInputIsInvalid = !enteredEmailIsValid && enteredEmailTouched
  
  let formIsValid = false

  if(enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true
  }


  const changeNameInputHandler = (event) => {
    setNameInputValue(event.target.value)
  }

  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true)
  }

  const changeEmailInputHandler = (event) => {
    setEmailInputValue(event.target.value)
  }

  const emailInputBlurHandler = (event) => {
    setEnteredEmailTouched(true)
  }

  const formSubmitHandler = (event) => {
    event.preventDefault()

    setEnteredNameTouched(true)
    setEnteredEmailTouched(true)

    if(!enteredNameIsValid || !enteredEmailIsValid) {
      console.log('disabled')
      return
    }

    setNameInputValue('')
    setEnteredNameTouched(false)

    setEmailInputValue('')
    setEnteredEmailTouched(false)

    console.log('sucsess')
  }

  let nameInputClases = nameInputIsInvalid ? 'form-control invalid' : 'form-control' 
  let emailInputClases = emailInputIsInvalid ? 'form-control invalid' : 'form-control'

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClases}>
        <label htmlFor='name'>Your Name</label>
        <input 
          type='text' 
          id='name' 
          onChange={changeNameInputHandler} 
          onBlur={nameInputBlurHandler}
          value={nameInputValue}/>
        {nameInputIsInvalid && <p className='error-text'>Name must not be empty.</p>}
      </div>

      <div className={emailInputClases}>
        <label htmlFor='email'>Your Email</label>
        <input 
          type='email' 
          id='email' 
          onChange={changeEmailInputHandler} 
          onBlur={emailInputBlurHandler}
          value={emailInputValue}/>
        {emailInputIsInvalid && <p className='error-text'>Please enter valid email.</p>}
      </div>

      <div className="form-actions">
        <button disabled={!formIsValid} >Submit</button>
        
      </div>
    </form>
  );
};

export default SimpleInput;
