import React, {useState} from 'react'

const SimpleInput = (props) => {
  const [nameInputValue, setNameInputValue] = useState('')
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false)
  const [enteredNameTouched, setEnteredNameTouched] = useState(false)

  // let inputNameIsValid = false

  const changeNameInputHandler = (event) => {
    setNameInputValue(event.target.value)
  }

  const formSubmitHandler = (event) => {
    event.preventDefault()
    setEnteredNameTouched(true)

    if(nameInputValue.trim() !== '') {
      setEnteredNameIsValid(true)
      console.log('name' + enteredNameIsValid)
    }
  }

  const nameInputIsValid = !enteredNameIsValid && enteredNameTouched
  let nameInputClases = nameInputIsValid ? 'form-control invalid' : 'form-control' 
  console.log(nameInputIsValid)

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClases}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={changeNameInputHandler} value={nameInputValue}/>
        {nameInputIsValid && <p className='error-text'>Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
