import React, {useState} from "react"

const useInput = (conditionFunc) => {
    const [enteredValue, setinputValue] = useState('')
    const [isTouched, setInputIsTouched] = useState(false)

    const valueIsValid = conditionFunc(inputValue)
    const hasError = !enteredValueIsValid && inputIsTouched

    const changeValueHandler = (event) => {
        setinputValue(event.target.value)
    }
    
    const inputBlurHandler = (event) => {
        setInputIsTouched(true)
    }

    const reset = () => {
        setinputValue('')
        setInputIsTouched(false)
    }

    return {
        enteredValue,
        isTouched,
        enteredValueIsValid,
        inputIsInvalid,
        changeInputValueHandler,
        inputBlurHandler,
        reset
        
    }
}

export default useInput
