import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkid'

const initialState = {
    counter: 0,
    hideCounter: false
}

const counterSlice = createSlice({
  name: 'counter',
  initialState,
  redusers: {
    increment(state) {
      state.counter++
    },
    decrement(state) {
      state.counter--
    },
    increce(state, action) {
      state.counter = state.counter + action.amount
    },
    toggleCounter(state) {
      state.hideCounter = !state.hideCounter
    }
  }
})



const store = createStore(counterReducer);

export default store;
