import { useSelector, useDispatch } from 'react-redux'

import { counterFunctions } from '../store/index'
import classes from './Counter.module.css';

const Counter = () => {
  const dispatch = useDispatch()
  const counter = useSelector(state => state.counter)
  const counterStatus = useSelector(state => state.hideCounter)

  const decrementHandler = () => {
    dispatch(counterFunctions.decrement())
  }

  const incrementHandler = () => {
    dispatch({type: 'increment'})
  }

  const increceHandler = () => {
    dispatch({type: 'increce', amount: 10})
  }
  
  const toggleCounterHandler = () => {
    dispatch({type: 'toggle'})
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {!counterStatus && <div className={classes.value}>{ counter }</div>}
      <div className={classes.controls}>
        <button onClick={decrementHandler}>Decrement</button>
        <button onClick={increceHandler}>Increce by 10</button>
        <button onClick={incrementHandler}>Increment</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
