import { createSlice, configureStore } from '@reduxjs/toolkit'

const initialState = {
    counter: 0,
    hideCounter: false
}

const counterSlice = createSlice({
  name: 'counter',
  initialState,
  redusers: {
    increment(state) {
      state.counter++
    },
    decrement(state) {
      state.counter--
    },
    increace(state, action) {
      state.counter = state.counter + action.payload
    },
    toggleCounter(state) {
      state.hideCounter = !state.hideCounter
    }
  }
})

const store = configureStore({
  reducer: counterSlice.reducer
})

export const counterFunctions = counterSlice.actions.toggleCounter()

export default store;
