import { useRef } from 'react'
import { useHistory } from 'react-router-dom'
import styles from './NewQuote.module.css'

const NewQuote = () => {
    const author = useRef()
    const quoteText = useRef()

    const history = useHistory()

    const addQuoteHandler = (event) => {
        event.preventDefault()

        console.log(author.current.value)
        console.log(quoteText.current.value)

        history.push('/')
    }

    return(
        <form className={styles.form} onSubmit={addQuoteHandler}>
            <div className={styles.name}>
                <label>Author name:</label>
                <input ref={author}></input>
            </div>
            <div className={styles.text}>
                <label>Quote text:</label>
                <textarea rows='10' ref={quoteText}></textarea>
            </div>
            <div className={styles.btn}>
                <button>Add Quote</button>
            </div>
        </form>
    )
}

export default NewQuote
