import { createSlice } from '@reduxjs/toolkit'

const quoteSlice = createSlice({
    name: 'quoteSlice',
    initialState: {
        quotes: []
    },
    reducers: {
        addItemToQuotes(state, actions) {
            const newQuote = actions.payload
            state.quotes.push(newQuote)
        },
        updateQuotes(state, actions) {
            const updatedQuote = actions.payload
            const test = [...state]
            const newState = state.quotes.find(quote => quote.id !== updatedQuote.id)
            
            console.log(newState)
            // state.push(...newState, updatedQuote)
        }
    }
})

export const quoteActions = quoteSlice.actions

export default quoteSlice
