import {useParams} from 'react-router-dom'

import styles from './DetailQuote'

import Detail from '../components/quote/Details'

const DetailQuote = (props) => {
    const params = useParams()
    const quote = props.quoteData.find(quote => quote.id === params.quoteId)

    if(!quote) {
        return <p className={styles.message}>No Quote Found</p>
    }

    console.log(quote)
    return(
        <Detail quote={quote}/>
    )
}

export default DetailQuote
