import {useParams, Route, Link} from 'react-router-dom'
import { useState } from 'react'

import styles from './DetailQuote.module.css'

import Container from '../UI/container'
import Detail from '../components/quote/Details'
import Comments from '../comments/Comments'

const DetailQuote = (props) => {
    const params = useParams()
    const [showComments, setShowComments] = useState(false) 

    const quote = props.quoteData.find(quote => quote.id === params.quoteId)
    let comments = []

    console.log(quote)

    const showCommentsHandler = () => {
        setShowComments(true)
    }
    
    const addCommentHandler = (commentText) => {
        if(quote.comments) {
            comments = quote.comments
        }
        
        comments.push(commentText)

        const updatedQuote = {...quote, comments}
        
        fetch(`https://educationdb-97121.firebaseio.com/quotes/${quote.id}.json`, {
            method: 'PUT',
            body: JSON.stringify(updatedQuote),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
             console.log(data)
        })
        .catch(error => {
            console.log(error)
        })
    }

    if(!quote) {
        return (
            <Container styles={styles.container}>
                <p className={styles.message}>No Quote Found</p>
            </Container>
        )
    }

    return(
        <Container>
            <Detail quote={quote}/>
            <div className={styles.centered}>
                {!showComments && 
                <Link to={`/detail/${params.quoteId}/comments`} onClick={showCommentsHandler}>
                    Add Comment
                </Link>
                }
            </div>
            <Route path={`/detail/${params.quoteId}/comments`}>
                <Comments onAddComment={addCommentHandler} quote={quote} allComments={quote}/>
            </Route>
        </Container>
    )
}

export default DetailQuote
